
/**
 * @module init
 */

import fs from 'fs-extra'
import path from 'path'
import confirm from 'confirm-simple'
import { log } from 'plugins'
import { guid, src, dist } from 'utils'

const base64CoverImage = '' // eslint-disable-line max-len

class Initialize {

  get src() {
    return src()
  }

  get dist() {
    return dist()
  }

  constructor() {
    this.removeConfigFile = Initialize.removeConfigFile

    this.dirs = [
      this.src,
      `${this.src}/_images`,
      `${this.src}/_javascripts`,
      `${this.src}/_stylesheets`,
      `${this.src}/_markdown`,
      `${this.src}/_fonts`,
      `${this.src}/.tmp`
    ]
    this.files = [{
      relpath: 'config.yml',
      content: `---
env: development # development | production
theme: default # name or path
src: ${path.basename(this.src)}
dist: ${path.basename(this.dist)}`
    }, {
      relpath: path.join(this.src, 'epub.yml'),
      content: '---'
    }, {
      relpath: path.join(this.src, 'mobi.yml'),
      content: '---'
    }, {
      relpath: path.join(this.src, 'sample.yml'),
      content: '---'
    }, {
      relpath: path.join(this.src, 'web.yml'),
      content: '---'
    }, {
      relpath: path.join(this.src, 'metadata.yml'),
      content: `---
-
  term: title
  value: Sample Book
  term_property: title-type
  term_property_value: main
-
  term: creator
  value: Author Name
  term_property: role
  term_property_value: aut
-
  term: contributor
  value: bber
  term_property: role
  term_property_value: ctb
-
  term: language
  value: en-US
-
  term: rights
  value: Sample Book Â© 2017
-
  term: format
  value: epub+zip
-
  term: date
  value: 2017-01-01
-
  term: publisher
  value: Triple Canopy
-
  term: date-modified
  value: 2017-01-01
-
  term: identifier
  value: ${guid()}
-
  term: cover
  value: _cover.jpg`
    }, {
      relpath: `${this.src}/_javascripts/application.js`,
      content: 'if (console && console.log) { console.log(\'Hello World!\') }'
    }, {
      relpath: `${this.src}/_markdown/00001.md`,
      content: `---
section_title: Chapter One
landmark_type: bodymatter
landmark_title: The Book
---
`
    }]
  }

  _makeDirs() {
    return new Promise((resolve, reject) =>
      this.dirs.map((_, i) =>
        fs.mkdirs(_, (err) => {
          if (err) { throw err }
          resolve()
        })
      )
    )
  }

  _writeFiles() {
    return new Promise((resolve, reject) =>
      this.files.map((_, i) =>
        fs.writeFile(_.relpath, _.content, (err) => {
          if (err) { throw err }
          resolve()
        })
      )
    )
  }

  _writeCoverImage() {
    return new Promise((resolve, reject) => {
      const buffer = new Buffer(base64CoverImage, 'base64')
      return fs.writeFile(`${this.src}/_images/cover.jpg`, buffer, (err) => {
        if (err) { throw err }
        resolve()
      })
    })
  }

  makeNewEbookTemplate(files) {
    return new Promise((resolve, reject) => {
      this._makeDirs()
      .then(this._writeFiles)
      .then(this._writeCoverImage)
      .catch(err => log.error(err))
      .then(resolve)
    })
  }

  /**
   * Remove the _stylesheet directory in the source directory
   * @return {Promise<Object|Error>}
   */
  cleanCSSDir() {
    return new Promise((resolve, reject) => {
      fs.remove(`${this.src}/_stylesheets`, (err) => {
        if (err) { reject(err) }
        resolve()
      })
    })
  }

  static removeConfigFile() {
    // necessary to remove the config to ensure that settings aren't carried
    // over from an existing project. there should be a better way of doing
    // this, though; a check that notifies users that they're in an existing
    // project directory, and a prompt to remove the existing data?
    return new Promise((resolve, reject) => {
      const configPath = path.join(process.cwd(), 'config.yml')
      const removeConfig = done =>
        fs.unlink(configPath, (err) => {
          if (err) { throw err }
          done()
        })
      if (fs.existsSync(configPath)) {
        log.warn('It looks like this is an active project directory, are you sure you want to overwrite?')
        if (process.env.NODE_ENV === 'test') { return removeConfig(resolve) }
        confirm('Overwite existing config?', (yes) => {
          if (!yes) { process.exit(0) }
          removeConfig(resolve)
        })
      } else {
        resolve()
      }
    })
  }

  /**
   * Write default directories and files to the source directory
   * @return {Promise<Object|Error>}
   */
  init() {
    return new Promise(resolve/* , reject */ =>
      this.removeConfigFile()
      .then(this.cleanCSSDir)
      .then(this.makeNewEbookTemplate)
      .catch(err => log.error(err))
      .then(resolve)
    )
  }
}

export default Initialize
